import React from "react";
import App from "../App";

function HandleInputs(){
    const playersTextInput = document.getElementById("player_text_input") as HTMLInputElement
    const playersStoryBoard = document.getElementById("player_story_board") as HTMLDivElement
    playersStoryBoard += playersTextInput.value
}

export default HandleInputs
