import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

export interface characterStats{
  stats:{
    strength: number;
    dex: number;
    con: number;
    int: number;
    wis: number;
    cha: number;
  }[]
}
function App() {
  const [statsPanel, setStatsPanel] = useState<characterStats["stats"]>([])
  return (
    <div className="App">
      <div className="main_board" id='player_character'>
          <p id='str'>Strength:</p>
          <p id='dex'>Dexterity:</p>
          <p id='con'>Constitution:</p>
          <p id='int'>Intelligence:</p>
          <p id='wis'>Wisdom:</p>
          <p id='cha'>Charisma:</p>
      </div>

      <div className="main_board" id='player_story_board'>
        <div id="players_story"></div>
        <div className="player_input_area" id='player_inputArea'>
          <textarea id="player_input"></textarea><button className='submit_button' id='player_submitButtom'>Enter</button>
        </div>

      </div>

      <div className="main_board" id='dm_story_board'>
        <div id="dm_choices"></div>
        <div className="player_input_area" id='dm_inputArea'>
        <textarea id="dm_input"></textarea><button className='submit_button' id='dm_submitButtom'>Enter</button>
        </div>
      </div>
    </div>
  );
}

export default App;
